$(document).ready(function() {
    function a() {
        $("body").scrollspy({
            target: ".scroll-nav",
            offset: 100
        })
    }
    $(function() {
        $(window).scroll(a), a()
    })
})

, $(document).ready(function() {
    $("body").on("click", '[href*="#"]', function(a) {
        var b = 100;
        $("html,body").stop().animate({
            scrollTop: $(this.hash).offset().top - b
        }, 1e3), a.preventDefault()
    })
}), $(function() {
    $("#sity li a").on("click", function() {
        var a = $(this).attr("data-sity"),
            b = $(this).attr("data-phone");
        $("#v_sity").html(a), $("#v_phone").html(b)
    })
}), $(function() {
    $(".small_img img").on("click", function() {
        var a = $(this).attr("src"),
            b = "" + a;
        $("#img-big").attr("src", b)
    })
})



function change_styles() {
  if ($(window).scrollTop() == 0) {
    var styles = {      
      'color': '#fff',
      'background': 'transparent'
    };
    $('#header__navigation').css(styles); 
    $('#header__navigation').css('position', 'relative'); 
    $('.line ').css( 'visibility', 'hidden');

    $('.header__phone').css( styles);
    $('.navbar-default .navbar-nav > li > a').css( styles);
    $('#v_sity').css( styles);
    $('div.dropdown').css('border', '1px solid #fff');
     $('.mark_icon').css( styles);
  } else {
    var styles = {      
      color: "#000"
    };
     
    $('#header__navigation').css('background', '#fff');
     $('#header__navigation').css('position', 'fixed'); 
    $('.header__phone').css( styles);
    $('.navbar-default .navbar-nav > li > a').css( styles);
    $('#v_sity').css( styles);
    $('div.dropdown').css('border', '1px solid #000');
    $('.mark_icon').css( styles);
    

    


   
 }
}
change_styles();

$(window).scroll(function() {
  change_styles();
});





      



