$(document).ready(function() {
    function a() {
        $("body").scrollspy({
            target: ".scroll-nav",
            offset: 100
        })
    }
    $(function() {
        $(window).scroll(a), a()
    })
})

, $(document).ready(function() {
    $("body").on("click", '[href*="#"]', function(a) {
        var b = 100;
        $("html,body").stop().animate({
            scrollTop: $(this.hash).offset().top - b
        }, 1e3), a.preventDefault()
    })
}), $(function() {
    $("#sity li a").on("click", function() {
        var a = $(this).attr("data-sity"),
            b = $(this).attr("data-phone");
        $("#v_sity").html(a), $("#v_phone").html(b)
    })
}), $(function() {
    $(".small_img img").on("click", function() {
        var a = $(this).attr("src"),
            b = "" + a;
        $("#img-big").attr("src", b)
    })
})





